import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class LoggerService {
  constructor() {}

  log(message: string): void {
    const timestamp = new Date().toISOString();
    const logEntry = `${timestamp} - ${message}`;

    const existingLogsString = localStorage.getItem('appLogs');
    let existingLogs: string[] = [];

    if (existingLogsString) {
      existingLogs = JSON.parse(existingLogsString);
    }

    existingLogs.push(logEntry);
    localStorage.setItem('appLogs', JSON.stringify(existingLogs));
  }

  getLogs(): string[] {
    const existingLogsString = localStorage.getItem('appLogs');
    if (existingLogsString) {
      return JSON.parse(existingLogsString);
    }

    return [];
  }
}
